def sumOfMatchingNeighbors(input_string):
    i = -1
    n = len(input_string)
    max_check = n -1
    sum =0

    while i < max_check:
        if input_string[i] == input_string[i+1]:
            sum += int(input_string[i])
        i += 1
    return sum 

